import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import './Signup.css';

function Signup() {
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const history = useHistory();

  const handleSignup = () => {
    console.log('Signup button clicked');
    // Perform signup logic
    history.push('/'); // Redirect to the home page after signup
  };

  const redirectToLogin = () => {
    history.push('/login'); // Replace "/login" with the actual URL of your login page
  };

  return (
    <div className="signup-container">
      <h2>Signup Page</h2>
      <form>
        <input
          type="text"
          placeholder="Username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <button type="button" onClick={handleSignup}>
          Signup
        </button>
        <p>
          Already have an account? <button type="button" onClick={redirectToLogin}>Login</button>
        </p>
      </form>
    </div>
  );
}

export default Signup;
