import React from 'react';
import styled from 'styled-components';
import avatar from '../../image/avatar.png';
import { signout } from '../../utils/icons';
import { menuItems } from '../../utils/menuitems';
import './Navigation.css';
import { useGlobalContext } from '../../context/globalContext'; // Import the useGlobalContext hook

function Navigation({ active, setActive }) {
  const { username } = useGlobalContext(); // Use the useGlobalContext hook to access the global state
  console.log('Navigation username:', username);// Use the useGlobalContext hook to access the global state

  return (
    <nav className='nav'>
      <div className="user-con">
        <img src={avatar} alt="" />
        <div className="text">
          <h2>{username}</h2>
          <p>Your Money</p>
        </div>
      </div>
      <ul className="menu-items">
        {menuItems.map((item) => (
          <li
            key={item.id}
            onClick={() => setActive(item.id)}
            className={active === item.id ? 'active' : ''}
          >
            {item.icon}
            <span>{item.title}</span>
          </li>
        ))}
      </ul>
      <div className="bottom-nav">
        <li>{signout} Sign Out</li>
      </div>
    </nav>
  );
}

export default Navigation;
