import React, { useState } from 'react';
import { useHistory } from 'react-router-dom';
import './Login.css';
import axios from 'axios';

const BASE_URL = "http://localhost:3001/api/v1/";

function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const history = useHistory();

  const handleLogin = async () => {
    try {
      const response = await axios.post(`${BASE_URL}login`, {
        email,
        password,
      });
      // You can handle the successful login here, e.g., store the token in localStorage and redirect to the dashboard page.
      history.push('/dashboard');
    } catch (error) {
      console.log('Login error:', error);
      // Handle login error, display error message, etc.
    }
  };

  const redirectToSignup = () => {
    history.push('/signup'); // Replace "/signup" with the actual URL of your signup page
  };

  return (
    <div className="login-container">
      <h2>Login Page</h2>
      <form>
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <button type="button" onClick={handleLogin}>
          Login
        </button>
        <p>
          Don't have an account? <a href="/signup" onClick={redirectToSignup}>Signup</a>
        </p>
      </form>
    </div>
  );
}

export default Login;
